INSERT INTO public.rules VALUES (1, 'join exchanges', 'find liquidity for given coins', 'https://mudia.com/_images/dash/stagetrader_engraved.png', 'Y 2020-09-15', '2020', 'if ((select count(id) from coins where coins.watch=true;) > 0)', 'enable marketdata of coins on exchanges and then do rule 8');
INSERT INTO public.rules VALUES (17, 'find edges', 'use technical analysis to find opportunities that provide an edge', 'https://mudia.com/_images/dash/stagetrader_engraved.png', 'Y 2020-09-15', '2020', 'for each TA indicator', 'trigger a signal');
INSERT INTO public.rules VALUES (18, 'set up alerts on charts', 'use tradingview.com alerts to signal', 'https://mudia.com/_images/dash/stagetrader_engraved.png', 'Y 2020-09-15', '2020', 'for each coin in coins', 'alerts signal inference-engine');
INSERT INTO public.rules VALUES (19, 'manage stop-loss orders', 'this is risk control', 'https://mudia.com/_images/dash/stagetrader_engraved.png', 'Y 2020-09-15', '2020', 'for each open trades', 'manage a stop-loss order');
INSERT INTO public.rules VALUES (20, 'use risk-ratio threshold', 'trade set-ups should be over the established risk-ratio threshold', 'https://mudia.com/_images/dash/stagetrader_engraved.png', 'Y 2020-09-15', '2020', 'if risk-ratio of trade < risk-ratio threshold', 'do not trade');
INSERT INTO public.rules VALUES (21, 'confirm the transactions of the trade', 'manually inspect the transaction info', 'https://mudia.com/_images/dash/stagetrader_engraved.png', 'Y 2020-09-15', '2020', 'for each transaction of each trade', 'confirm the transaction and mark it as confirmed');
INSERT INTO public.rules VALUES (22, 'consider several time increments', 'scale the indicators', 'https://mudia.com/_images/dash/stagetrader_engraved.png', 'Y 2020-09-15', '2020', 'for each indicator with time', 'scale the time');
INSERT INTO public.rules VALUES (9, 'analyze coins', 'rank the cryptocurrencies and maintain watchlist of coins', 'https://mudia.com/_images/dash/stagetrader_engraved.png', 'Y 2020-11-21', '2020', 'if cryptocurrency matches tradeable criteria', 'then set coins watch=true');
INSERT INTO public.rules VALUES (11, 'start stagetrader operator session', 'start here', 'https://mudia.com/_images/dash/stagetrader_engraved.png', 'Y 2020-09-13', '2020', 'if starting stagetrader work session', 'then do rule where id=9');
INSERT INTO public.rules VALUES (8, 'monitor marketdata feeds', 'stagetrader marketdatabot', 'https://mudia.com/_images/dash/stagetrader_engraved.png', 'Y 2020-09-13', '2020', 'if errors or server stress', 'debug the situation and then do rule 2');
INSERT INTO public.rules VALUES (10, 'second order stats', 'use marketdata to create 2nd order stats', 'https://mudia.com/_images/dash/stagetrader_engraved.png', 'Y 2020-09-13', '2020', NULL, NULL);
INSERT INTO public.rules VALUES (3, 'define transaction risks', 'a state of not knowing, follow the 5 Fundamental Truths', 'https://mudia.com/_images/dash/stagetrader_engraved.png', 'Y 2020-09-06', '2020', NULL, NULL);
INSERT INTO public.rules VALUES (4, 'close the trade', 'time the perfect trade', 'https://mudia.com/_images/dash/stagetrader_engraved.png', 'Y 2020-09-06', '2020', NULL, NULL);
INSERT INTO public.rules VALUES (5, 'manage expections', 'do full double-entry accounting, tex prep', 'https://mudia.com/_images/dash/stagetrader_engraved.png', 'Y 2020-09-06', '2020', NULL, NULL);
INSERT INTO public.rules VALUES (2, 'pattern recognition', 'sit a river, analysis of coins, data feed, secord order stats', 'https://mudia.com/_images/dash/stagetrader_engraved.png', 'Y 2020-09-13', '2020', NULL, NULL);
INSERT INTO public.rules VALUES (12, 'breakout on volume', 'standard signal combining price and volume', 'https://mudia.com/_images/dash/stagetrader_engraved.png', 'Y 2020-09-15', '2020', 'price is greater than resistance line accompanied by upward-sloping MA of price', 'buy and wait for pullback');
INSERT INTO public.rules VALUES (13, 'manage wallets', 'maintain the software that enables storage of cryptocurrencies', 'https://mudia.com/_images/dash/stagetrader_engraved.png', 'Y 2020-09-15', '2020', 'for each coin in coins', 'maintain wallet software');
INSERT INTO public.rules VALUES (14, 'use cryptocurrency', 'engage with ecosystem and do a transaction', 'https://mudia.com/_images/dash/stagetrader_engraved.png', 'Y 2020-09-15', '2020', 'for each coin in coins', 'spend the coin on-chain');
INSERT INTO public.rules VALUES (15, 'fund exchanges', 'make deposit', 'https://mudia.com/_images/dash/stagetrader_engraved.png', 'Y 2020-09-15', '2020', 'for each exchange in exchanges', 'deposit cryptocurrency');
INSERT INTO public.rules VALUES (16, 'calculate trade size', 'consider statistics and sampling', 'https://mudia.com/_images/dash/stagetrader_engraved.png', 'Y 2020-09-15', '2020', 'for each week', 'publish a report with trades statistics');
